INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2018-01-15 15:06:12', '2018-01-15 15:06:12', 'Chúc mừng đến với WordPress. Đây là bài viết đầu tiên của bạn. Hãy chỉnh sửa hay xóa bài viết này, và bắt đầu viết blog!', 'Chào tất cả mọi người!', '', 'publish', 'open', 'open', '', 'chao-moi-nguoi', '', '', '2018-01-15 15:06:12', '2018-01-15 15:06:12', '', 0, 'http://localhost/PTN_HTTT/?p=1', 0, 'post', '', 1),
(2, 1, '2018-01-15 15:06:12', '2018-01-15 15:06:12', 'Đây là một trang mẫu. Nó khác với một bài blog bởi vì nó sẽ là một trang tĩnh và sẽ được thêm vào thanh menu của trang web của bạn (trong hầu hết theme). Mọi người thường bắt đầu bằng một trang Giới thiệu để giới thiệu bản thân đến người dùng tiềm năng. Bạn có thể viết như sau:\n\n<blockquote>Xin chào! Tôi là người giao thư bằng xe đạp vào ban ngày, một diễn viên đầy tham vọng vào ban đêm, và đây là trang web của tôi. Tôi sống ở Los Angeles, có một chú cho tuyệt vời tên là Jack, và tôi thích uống cocktail.</blockquote>\n\n...hay như thế này:\n\n<blockquote>Công ty XYZ Doohickey được thành lập vào năm 1971, và đã cung cấp đồ dùng chất lượng cho công chúng kể từ đó. Nằm ở thành phố Gotham, XYZ tạo việc làm cho hơn 2.000 người và làm tất cả những điều tuyệt vời cho cộng đồng Gotham.</blockquote>\n\nLà người dùng WordPress mới, bạn nên truy cập <a href="http://localhost/PTN_HTTT/wp-admin/">trang quản trị</a> để xóa trang này và tạo các trang mới cho nội dung của bạn. Chúc vui vẻ!', 'Trang Mẫu', '', 'publish', 'closed', 'open', '', 'Trang mẫu', '', '', '2018-01-15 15:06:12', '2018-01-15 15:06:12', '', 0, 'http://localhost/PTN_HTTT/?page_id=2', 0, 'page', '', 0),
(3, 1, '2018-01-15 15:27:37', '0000-00-00 00:00:00', '', 'Lưu bản nháp tự động', '', 'auto-draft', 'open', 'open', '', '', '', '', '2018-01-15 15:27:37', '0000-00-00 00:00:00', '', 0, 'http://localhost/PTN_HTTT/?p=3', 0, 'post', '', 0),
(4, 1, '2018-01-15 15:31:54', '0000-00-00 00:00:00', '', 'Espresso', '', 'auto-draft', 'open', 'closed', '', '', '', '', '2018-01-15 15:27:53', '0000-00-00 00:00:00', '', 0, 'http://localhost/PTN_HTTT/wp-content/uploads/2018/01/espresso.jpg', 0, 'attachment', 'image/jpeg', 0),
(5, 1, '2018-01-15 15:31:54', '0000-00-00 00:00:00', '', 'Bánh kẹp', '', 'auto-draft', 'open', 'closed', '', '', '', '', '2018-01-15 15:27:55', '0000-00-00 00:00:00', '', 0, 'http://localhost/PTN_HTTT/wp-content/uploads/2018/01/sandwich.jpg', 0, 'attachment', 'image/jpeg', 0),
